document.addEventListener("DOMContentLoaded",function(){console.log("Carregando sidebar..."),fetch("../../app/pages/main-page/main-page.component.html").then(e=>e.text()).then(e=>{document.getElementById("sidebar-container").innerHTML=e,console.log("Sidebar carregada com sucesso!");const t=document.createElement("link");t.rel="stylesheet",t.href="components/sidebar.css",document.head.appendChild(t);const n=document.getElementById("toggle-sidebar"),o=document.querySelector(".sidebar");n.addEventListener("click",function(){o.classList.toggle("active")})}).catch(e=>console.error("Erro ao carregar a Sidebar:",e))});